import express from "express";
import { storage } from "../storage";
import { icd10CodeService } from "../services/icd10-codes";
import { authenticate, AuthenticatedRequest } from "../middleware/auth";
import { appLogger } from "../services/app-logger";
import { auditService } from "../services/audit";

const router = express.Router();

export function setupIcd10CodesRoutes(app: express.Application) {
  // Get all ICD-10 codes with search and filtering
  app.get("/api/icd10-codes", authenticate, async (req: AuthenticatedRequest, res) => {
    try {
      const { search, category, chapter, limit = 50 } = req.query;

      let codes;
      if (search) {
        codes = await icd10CodeService.searchIcd10Codes(
          search as string,
          category as string | undefined
        );
      } else if (category) {
        codes = await icd10CodeService.getIcd10CodesByCategory(category as string);
      } else if (chapter) {
        codes = await icd10CodeService.getIcd10CodesByChapter(chapter as string);
      } else {
        codes = await storage.getIcd10Codes(parseInt(limit as string));
      }

      await auditService.log(req.user!.id, 'ICD10_CODES_ACCESS', 'icd10_code', null, {
        search: search as string,
        category: category as string,
        chapter: chapter as string,
        limit: parseInt(limit as string),
        resultsCount: codes.length,
      }, req.ip || '', req.get("User-Agent") || '');

      await appLogger.info('ICD-10 codes retrieved', { component: 'api', userId: req.user?.id });
      res.json(codes);
    } catch (error) {
      await appLogger.error('Failed to get ICD-10 codes', { component: 'api', userId: req.user?.id }, error as Error);
      res.status(500).json({ message: "Failed to get ICD-10 codes" });
    }
  });

  // Get ICD-10 categories
  app.get("/api/icd10-categories", authenticate, async (req: AuthenticatedRequest, res) => {
    try {
      const categories = await icd10CodeService.getIcd10Categories();

      await auditService.log(req.user!.id, 'ICD10_CATEGORIES_ACCESS', 'icd10_code', null, {
        categoriesCount: categories.length,
      }, req.ip || '', req.get("User-Agent") || '');

      res.json(categories);
    } catch (error) {
      await appLogger.error('Failed to get ICD-10 categories', { component: 'api', userId: req.user?.id }, error as Error);
      res.status(500).json({ message: "Failed to get ICD-10 categories" });
    }
  });

  // Get specific ICD-10 code details
  app.get("/api/icd10-codes/:code", authenticate, async (req: AuthenticatedRequest, res) => {
    try {
      const code = req.params.code;
      const diagnosis = await icd10CodeService.getDiagnosisInfo(code);

      if (!diagnosis) {
        return res.status(404).json({ message: "ICD-10 code not found" });
      }

      await auditService.log(req.user!.id, 'ICD10_CODE_DETAIL_ACCESS', 'icd10_code', null, {
        code,
        description: diagnosis.description,
        category: diagnosis.category,
      }, req.ip || '', req.get("User-Agent") || '');

      res.json(diagnosis);
    } catch (error) {
      await appLogger.error('Failed to get ICD-10 code details', { component: 'api', userId: req.user?.id }, error as Error);
      res.status(500).json({ message: "Failed to get ICD-10 code details" });
    }
  });

  // Search ICD-10 codes with advanced filtering
  app.post("/api/icd10-codes/search", authenticateUser, async (req, res) => {
    try {
      const { query, filters } = req.body;
      const { category, chapter, fiscalYear } = filters || {};

      let codes;
      if (chapter) {
        codes = await icd10CodeService.getIcd10CodesByChapter(chapter);
      } else if (category) {
        codes = await icd10CodeService.getIcd10CodesByCategory(category);
      } else {
        codes = await icd10CodeService.searchIcd10Codes(query, category);
      }

      // Apply additional filters if specified
      if (fiscalYear) {
        codes = codes.filter(code => code.fiscalYear === fiscalYear);
      }

      res.json(codes);
    } catch (error) {
      await appLogger.error('Failed to search ICD-10 codes', 'api', req.user?.id, error);
      res.status(500).json({ message: "Failed to search ICD-10 codes" });
    }
  });

  // Add custom ICD-10 code (admin only)
  app.post("/api/icd10-codes", authenticateUser, async (req, res) => {
    try {
      const userId = req.user?.id;

      if (!userId) {
        return res.status(401).json({ message: "Authentication required" });
      }

      // Check if user has admin role
      const user = await storage.getUser(userId);
      if (!user || user.role !== 'admin') {
        return res.status(403).json({ message: "Admin access required" });
      }

      const codeData = req.body;
      const newCode = await icd10CodeService.addCustomIcd10Code(codeData, userId);

      await appLogger.info(`Custom ICD-10 code added: ${newCode.icd10Code}`, 'api', userId);
      res.status(201).json(newCode);
    } catch (error) {
      await appLogger.error('Failed to add ICD-10 code', 'api', req.user?.id, error);
      res.status(500).json({ message: "Failed to add ICD-10 code" });
    }
  });

  // Update ICD-10 code (admin only)
  app.put("/api/icd10-codes/:code", authenticateUser, async (req, res) => {
    try {
      const userId = req.user?.id;

      if (!userId) {
        return res.status(401).json({ message: "Authentication required" });
      }

      // Check if user has admin role
      const user = await storage.getUser(userId);
      if (!user || user.role !== 'admin') {
        return res.status(403).json({ message: "Admin access required" });
      }

      const code = req.params.code;
      const updateData = req.body;
      const updatedCode = await icd10CodeService.updateIcd10Code(code, updateData, userId);

      await appLogger.info(`ICD-10 code updated: ${code}`, 'api', userId);
      res.json(updatedCode);
    } catch (error) {
      await appLogger.error('Failed to update ICD-10 code', 'api', req.user?.id, error);
      res.status(500).json({ message: "Failed to update ICD-10 code" });
    }
  });
}

export default router;

server/routes/insurance.ts.content:
import { Router } from "express";
import { z } from "zod";
import { storage } from "../storage";
import { insertInsuranceProviderSchema } from "@shared/schema";
import { authenticate, AuthenticatedRequest } from "../middleware/auth";
import { auditService } from "../services/audit";

const router = Router();

// Get all insurance providers
router.get("/providers", authenticate, async (req: AuthenticatedRequest, res) => {
  try {
    const providers = await storage.getInsuranceProviders();

    await auditService.log(req.user!.id, 'INSURANCE_PROVIDER_LIST', 'insurance_provider', null, {
      providerCount: providers.length,
    }, req.ip || '', req.get("User-Agent") || '');

    res.json(providers);
  } catch (error) {
    console.error("Error fetching insurance providers:", error);
    res.status(500).json({ message: "Failed to fetch insurance providers" });
  }
});

// Get single insurance provider
router.get("/providers/:id", authenticate, async (req: AuthenticatedRequest, res) => {
  try {
    const id = parseInt(req.params.id);
    if (isNaN(id)) {
      return res.status(400).json({ message: "Invalid provider ID" });
    }

    const provider = await storage.getInsuranceProvider(id);
    if (!provider) {
      return res.status(404).json({ message: "Insurance provider not found" });
    }

    await auditService.log(req.user!.id, 'INSURANCE_PROVIDER_VIEW', 'insurance_provider', id, {
      providerName: provider.name,
      providerCode: provider.code,
    }, req.ip || '', req.get("User-Agent") || '');

    res.json(provider);
  } catch (error) {
    console.error("Error fetching insurance provider:", error);
    res.status(500).json({ message: "Failed to fetch insurance provider" });
  }
});

// Create insurance provider
router.post("/providers", authenticate, async (req: AuthenticatedRequest, res) => {
  try {
    // Only admin can create insurance providers
    if (req.user?.role !== "admin") {
      await auditService.log(req.user!.id, 'INSURANCE_PROVIDER_CREATE_DENIED', 'insurance_provider', null, {
        reason: 'Insufficient permissions',
        userRole: req.user?.role,
      }, req.ip || '', req.get("User-Agent") || '');

      return res.status(403).json({ message: "Admin access required" });
    }

    const validatedData = insertInsuranceProviderSchema.parse(req.body);
    const provider = await storage.createInsuranceProvider(validatedData);

    await auditService.log(req.user!.id, 'INSURANCE_PROVIDER_CREATE', 'insurance_provider', provider.id, {
      providerName: provider.name,
      providerCode: provider.code,
      contactInfo: provider.contactInfo,
      priorAuthSteps: provider.priorAuthSteps,
    }, req.ip || '', req.get("User-Agent") || '');

    res.status(201).json(provider);
  } catch (error) {
    if (error instanceof z.ZodError) {
      return res.status(400).json({ 
        message: "Validation error", 
        errors: error.errors 
      });
    }
    console.error("Error creating insurance provider:", error);
    res.status(500).json({ message: "Failed to create insurance provider" });
  }
});

// Update insurance provider
router.put("/providers/:id", authenticate, async (req: AuthenticatedRequest, res) => {
  try {
    // Only admin can update insurance providers
    if (req.user?.role !== "admin") {
      await auditService.log(req.user!.id, 'INSURANCE_PROVIDER_UPDATE_DENIED', 'insurance_provider', parseInt(req.params.id), {
        reason: 'Insufficient permissions',
        userRole: req.user?.role,
      }, req.ip || '', req.get("User-Agent") || '');

      return res.status(403).json({ message: "Admin access required" });
    }

    const id = parseInt(req.params.id);
    if (isNaN(id)) {
      return res.status(400).json({ message: "Invalid provider ID" });
    }

    const existingProvider = await storage.getInsuranceProvider(id);
    if (!existingProvider) {
      return res.status(404).json({ message: "Insurance provider not found" });
    }

    const validatedData = insertInsuranceProviderSchema.partial().parse(req.body);
    const updatedProvider = await storage.updateInsuranceProvider(id, validatedData);

    if (!updatedProvider) {
      return res.status(404).json({ message: "Failed to update insurance provider" });
    }

    await auditService.log(req.user!.id, 'INSURANCE_PROVIDER_UPDATE', 'insurance_provider', id, {
      changes: validatedData,
      previousData: {
        name: existingProvider.name,
        code: existingProvider.code,
        contactInfo: existingProvider.contactInfo,
      },
      newData: {
        name: updatedProvider.name,
        code: updatedProvider.code,
        contactInfo: updatedProvider.contactInfo,
      },
    }, req.ip || '', req.get("User-Agent") || '');

    res.json(updatedProvider);
  } catch (error) {
    if (error instanceof z.ZodError) {
      return res.status(400).json({ 
        message: "Validation error", 
        errors: error.errors 
      });
    }
    console.error("Error updating insurance provider:", error);
    res.status(500).json({ message: "Failed to update insurance provider" });
  }
});

// Delete insurance provider (soft delete)
router.delete("/providers/:id", authenticate, async (req: AuthenticatedRequest, res) => {
  try {
    // Only admin can delete insurance providers
    if (req.user?.role !== "admin") {
      await auditService.log(req.user!.id, 'INSURANCE_PROVIDER_DELETE_DENIED', 'insurance_provider', parseInt(req.params.id), {
        reason: 'Insufficient permissions',
        userRole: req.user?.role,
      }, req.ip || '', req.get("User-Agent") || '');

      return res.status(403).json({ message: "Admin access required" });
    }

    const id = parseInt(req.params.id);
    if (isNaN(id)) {
      return res.status(400).json({ message: "Invalid provider ID" });
    }

    const existingProvider = await storage.getInsuranceProvider(id);
    if (!existingProvider) {
      return res.status(404).json({ message: "Insurance provider not found" });
    }

    const success = await storage.deleteInsuranceProvider(id);
    if (!success) {
      return res.status(500).json({ message: "Failed to delete insurance provider" });
    }

    await auditService.log(req.user!.id, 'INSURANCE_PROVIDER_DELETE', 'insurance_provider', id, {
      deletedProvider: {
        name: existingProvider.name,
        code: existingProvider.code,
        contactInfo: existingProvider.contactInfo,
      },
    }, req.ip || '', req.get("User-Agent") || '');

    res.json({ message: "Insurance provider deleted successfully" });
  } catch (error) {
    console.error("Error deleting insurance provider:", error);
    res.status(500).json({ message: "Failed to delete insurance provider" });
  }
});

export default router;

server/routes/modmed-ema.ts.content:
import { Router } from "express";
import { z } from "zod";
import { authenticate, AuthenticatedRequest } from "../middleware/auth";
import { auditService } from "../services/audit";
import { storage } from "../storage";
import { nanoid } from "nanoid";

const router = Router();

// Validation schemas
const emaCredentialsSchema = z.object({
  clientId: z.string().min(1, "Client ID is required"),
  clientSecret: z.string().min(1, "Client Secret is required"),
  baseUrl: z.string().url("Valid base URL is required"),
  environment: z.enum(["production", "sandbox"])
});

const importPatientsSchema = z.object({
  patientIds: z.array(z.string()).min(1, "At least one patient ID is required"),
  baseUrl: z.string().url("Valid base URL is required")
});

// ModMed EMA OAuth2 Authentication
router.post("/authenticate", authenticate, async (req: AuthenticatedRequest, res) => {
  try {
    const credentials = emaCredentialsSchema.parse(req.body);

    // OAuth2 authentication with ModMed EMA
    const authUrl = credentials.environment === 'production' 
      ? 'https://auth-exscribe-prod-fhir.ema-api.com/core/oauth2/token'
      : 'https://auth-exscribe-sandbox-fhir.ema-api.com/core/oauth2/token';

    const authResponse = await fetch(authUrl, {
      method: 'POST',
      headers: {
        'Content-Type': 'application/x-www-form-urlencoded',
        'Accept': 'application/json'
      },
      body: new URLSearchParams({
        grant_type: 'client_credentials',
        client_id: credentials.clientId,
        client_secret: credentials.clientSecret,
        scope: 'patient/*.read practitioner/*.read organization/*.read'
      }).toString()
    });

    if (!authResponse.ok) {
      const errorText = await authResponse.text();
      console.error('ModMed EMA authentication failed:', errorText);

      await auditService.log(req.user!.id, 'MODMED_EMA_AUTH_FAILED', 'system', null, {
        environment: credentials.environment,
        error: errorText
      }, req.ip || '', req.get("User-Agent") || '');

      return res.status(401).json({ 
        message: "ModMed EMA authentication failed", 
        details: errorText 
      });
    }

    const authData = await authResponse.json();

    await auditService.log(req.user!.id, 'MODMED_EMA_AUTH_SUCCESS', 'system', null, {
      environment: credentials.environment,
      baseUrl: credentials.baseUrl,
      tokenExpiry: authData.expires_in
    }, req.ip || '', req.get("User-Agent") || '');

    res.json({
      access_token: authData.access_token,
      token_type: authData.token_type,
      expires_in: authData.expires_in,
      scope: authData.scope
    });

  } catch (error: any) {
    console.error('ModMed EMA authentication error:', error);

    if (error instanceof z.ZodError) {
      return res.status(400).json({ 
        message: "Invalid credentials format", 
        errors: error.errors 
      });
    }

    res.status(500).json({ 
      message: "Authentication failed", 
      error: error.message 
    });
  }
});

// Fetch FHIR metadata to test connection
router.get("/metadata", authenticate, async (req: AuthenticatedRequest, res) => {
  try {
    const emaToken = req.get('EMA-Token');
    if (!emaToken) {
      return res.status(401).json({ message: "EMA token required" });
    }

    // Default to production metadata endpoint
    const metadataUrl = 'https://exscribe-prod-fhir.ema-api.com/fhir/modmed/root/r4/metadata';

    const metadataResponse = await fetch(metadataUrl, {
      method: 'GET',
      headers: {
        'Authorization': `Bearer ${emaToken}`,
        'Accept': 'application/fhir+json'
      }
    });

    if (!metadataResponse.ok) {
      const errorText = await metadataResponse.text();
      console.error('ModMed EMA metadata fetch failed:', errorText);
      return res.status(metadataResponse.status).json({ 
        message: "Failed to fetch metadata", 
        details: errorText 
      });
    }

    const metadata = await metadataResponse.json();

    await auditService.log(req.user!.id, 'MODMED_EMA_METADATA_FETCH', 'system', null, {
      metadataVersion: metadata.fhirVersion,
      implementation: metadata.implementation?.description
    }, req.ip || '', req.get("User-Agent") || '');

    res.json(metadata);

  } catch (error: any) {
    console.error('ModMed EMA metadata error:', error);
    res.status(500).json({ 
      message: "Failed to fetch metadata", 
      error: error.message 
    });
  }
});

// Fetch patients from ModMed EMA FHIR API
router.get("/patients", authenticate, async (req: AuthenticatedRequest, res) => {
  try {
    const emaToken = req.get('EMA-Token');
    if (!emaToken) {
      return res.status(401).json({ message: "EMA token required" });
    }

    // Fetch patients with pagination support
    const limit = parseInt(req.query.limit as string) || 50;
    const offset = parseInt(req.query.offset as string) || 0;

    const patientsUrl = `https://exscribe-prod-fhir.ema-api.com/fhir/modmed/root/r4/Patient?_count=${limit}&_offset=${offset}`;

    const patientsResponse = await fetch(patientsUrl, {
      method: 'GET',
      headers: {
        'Authorization': `Bearer ${emaToken}`,
        'Accept': 'application/fhir+json'
      }
    });

    if (!patientsResponse.ok) {
      const errorText = await patientsResponse.text();
      console.error('ModMed EMA patients fetch failed:', errorText);
      return res.status(patientsResponse.status).json({ 
        message: "Failed to fetch patients", 
        details: errorText 
      });
    }

    const patientsBundle = await patientsResponse.json();
    const patients = patientsBundle.entry?.map((entry: any) => entry.resource) || [];

    await auditService.log(req.user!.id, 'MODMED_EMA_PATIENTS_FETCH', 'system', null, {
      patientsCount: patients.length,
      totalResults: patientsBundle.total
    }, req.ip || '', req.get("User-Agent") || '');

    res.json({
      patients,
      total: patientsBundle.total,
      limit,
      offset
    });

  } catch (error: any) {
    console.error('ModMed EMA patients fetch error:', error);
    res.status(500).json({ 
      message: "Failed to fetch patients", 
      error: error.message 
    });
  }
});

// Import selected patients from ModMed EMA to MedAuth Pro
router.post("/import-patients", authenticate, async (req: AuthenticatedRequest, res) => {
  try {
    const emaToken = req.get('EMA-Token');
    if (!emaToken) {
      return res.status(401).json({ message: "EMA token required" });
    }

    const { patientIds, baseUrl } = importPatientsSchema.parse(req.body);

    let recordsImported = 0;
    let recordsUpdated = 0;
    let errors: string[] = [];
    const importedPatients: any[] = [];

    for (const patientId of patientIds) {
      try {
        // Fetch detailed patient data from ModMed EMA
        const patientUrl = `${baseUrl}/Patient/${patientId}`;
        const patientResponse = await fetch(patientUrl, {
          method: 'GET',
          headers: {
            'Authorization': `Bearer ${emaToken}`,
            'Accept': 'application/fhir+json'
          }
        });

        if (!patientResponse.ok) {
          errors.push(`Failed to fetch patient ${patientId}: ${patientResponse.statusText}`);
          continue;
        }

        const fhirPatient = await patientResponse.json();

        // Convert FHIR patient to MedAuth Pro format
        const medAuthPatient = {
          patientId: nanoid(),
          firstName: fhirPatient.name?.[0]?.given?.[0] || 'Unknown',
          lastName: fhirPatient.name?.[0]?.family || 'Unknown',
          dateOfBirth: new Date(fhirPatient.birthDate || '1900-01-01'),
          gender: fhirPatient.gender || null,
          phone: fhirPatient.telecom?.find((t: any) => t.system === 'phone')?.value || null,
          email: fhirPatient.telecom?.find((t: any) => t.system === 'email')?.value || null,
          address: fhirPatient.address?.[0] ? [
            fhirPatient.address[0].line?.join(' '),
            fhirPatient.address[0].city,
            fhirPatient.address[0].state,
            fhirPatient.address[0].postalCode
          ].filter(Boolean).join(', ') : null,
          city: fhirPatient.address?.[0]?.city || null,
          state: fhirPatient.address?.[0]?.state || null,
          zipCode: fhirPatient.address?.[0]?.postalCode || null,
          race: fhirPatient.extension?.find((e: any) => 
            e.url === 'http://hl7.org/fhir/us/core/StructureDefinition/us-core-race'
          )?.extension?.[0]?.valueCoding?.display || null,
          ethnicity: fhirPatient.extension?.find((e: any) => 
            e.url === 'http://hl7.org/fhir/us/core/StructureDefinition/us-core-ethnicity'
          )?.extension?.[0]?.valueCoding?.display || null,
          sourceSystem: 'ModMed EMA',
          lastImportDate: new Date()
        };

        // Check for existing patient by name and DOB
        const existingPatient = await storage.getPatientByName(
          medAuthPatient.firstName,
          medAuthPatient.lastName,
          medAuthPatient.dateOfBirth
        );

        if (existingPatient) {
          // Update existing patient
          await storage.updatePatient(existingPatient.id, medAuthPatient);
          recordsUpdated++;

          await auditService.log(req.user!.id, 'MODMED_EMA_PATIENT_UPDATE', 'patient', existingPatient.id, {
            fhirPatientId: patientId,
            patientName: `${medAuthPatient.firstName} ${medAuthPatient.lastName}`,
            sourceSystem: 'ModMed EMA'
          }, req.ip || '', req.get("User-Agent") || '', existingPatient, medAuthPatient);
        } else {
          // Create new patient
          const newPatient = await storage.createPatient(medAuthPatient);
          recordsImported++;
          importedPatients.push(newPatient);

          await auditService.log(req.user!.id, 'MODMED_EMA_PATIENT_CREATE', 'patient', newPatient.id, {
            fhirPatientId: patientId,
            patientName: `${medAuthPatient.firstName} ${medAuthPatient.lastName}`,
            sourceSystem: 'ModMed EMA'
          }, req.ip || '', req.get("User-Agent") || '', null, newPatient);
        }

      } catch (patientError: any) {
        console.error(`Error importing patient ${patientId}:`, patientError);
        errors.push(`Patient ${patientId}: ${patientError.message}`);
      }
    }

    await auditService.log(req.user!.id, 'MODMED_EMA_IMPORT_COMPLETE', 'system', null, {
      totalRequested: patientIds.length,
      recordsImported,
      recordsUpdated,
      errorCount: errors.length,
      sourceSystem: 'ModMed EMA'
    }, req.ip || '', req.get("User-Agent") || '');

    res.json({
      recordsProcessed: patientIds.length,
      recordsImported,
      recordsUpdated,
      errors: errors.length,
      errorDetails: errors,
      patients: importedPatients
    });

  } catch (error: any) {
    console.error('ModMed EMA import error:', error);

    if (error instanceof z.ZodError) {
      return res.status(400).json({ 
        message: "Invalid import request", 
        errors: error.errors 
      });
    }

    res.status(500).json({ 
      message: "Import failed", 
      error: error.message 
    });
  }
});

export default router;

